mapper.select:
select t.l_id, t.vc_name, t.l_sex, t.l_tel, t.vc_mail,
       t.vc_address, t.d_jrsj, t.l_state
from TUSER t where t.l_id = #{id}

select t.l_id, t.vc_name, t.l_sex, t.l_tel, t.vc_mail,
       t.vc_address, t.d_jrsj, t.l_state
from TUSER t where t.vc_name = #{vc_name}

update TUSER t
   set t.vc_name = #{vc_name},
       t.vc_mail = #{vc_mail},
       t.vc_address = #{vc_address}
 where t.l_id = #{l_id}
